    private String[] dateList = { "2020/04/12", "2020/04/13", "2020/04/14", "2020/04/15", "2020/04/16", "2020/04/17", "2020/04/18" };

    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_sleep, null);
        v = view;
        return view;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        initView();
        initChart();

        sleepCalendarView.setSelectionMode(MaterialCalendarView.SELECTION_MODE_MULTIPLE);
        for(int i = 0;i<dateList.length;i++)
        {
            SimpleDateFormat sdf = new SimpleDateFormat("yyyy/MM/dd");
            try {
                Date date = sdf.parse(dateList[i]);
                sleepCalendarView.setDateSelected(date, true);
            } catch (ParseException e) {
                e.printStackTrace();
            }
        }
    }

    private void initView() {
        dialySleepChart = v.findViewById(R.id.dialySleepChart);
        sleepTime = v.findViewById(R.id.sleepTime);
        sleepQuality = v.findViewById(R.id.sleepQuality);
        sleepLatancy = v.findViewById(R.id.sleepLatency);
        sleepCalendarView = v.findViewById(R.id.sleepCalendarView);
        startTime = v.findViewById(R.id.startTime);
        endTime = v.findViewById(R.id.endTime);

        sleepCalendarView.setOnDateChangedListener(new OnDateSelectedListener() {
            @Override
            public void onDateSelected(@NonNull MaterialCalendarView widget, @NonNull CalendarDay date, boolean selected) {
                SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy/MM/dd");
                String select_day = dateFormat.format(date.getDate());
                sleepCalendarView.setDateSelected(date, !selected);
                switch (select_day) {
                    case "2020/04/12":
                        Index = 0;
                        break;
                    case "2020/04/13":
                        Index = 1;
                        break;
                    case "2020/04/14":
                        Index = 2;
                        break;
                    case "2020/04/15":
                        Index = 3;
                        break;
                    case "2020/04/16":
                        Index = 4;
                        break;
                    case "2020/04/17":
                        Index = 5;
                        break;
                    case "2020/04/18":
                        Index = 6;
                        break;
                }
                sleepTime.setText(time[Index]);
                sleepQuality.setText(SE[Index]);
                sleepLatancy.setText(SOT[Index]);
                String[] s = time[Index].split("～");
                startTime.setText(s[0]);
                endTime.setText(s[1]);
                ArrayList<BarEntry> barEntries = new ArrayList<>();
                for (int i = 0; i < stage[Index].length; i++) {
                    if (stage[Index][i] == 0) {
                        barEntries.add(new BarEntry(i, 1));
                    } else {
                        barEntries.add(new BarEntry(i, 0));
                    }
                }
                BarDataSet bardataset = new BarDataSet(barEntries, "Sleep");
                bardataset.setColor(ContextCompat.getColor(v.getContext(), R.color.colorPrimary));
                BarData data = new BarData(bardataset);
                data.setDrawValues(false);
                dialySleepChart.setData(data);
                dialySleepChart.animateY(1000);
            }
        });
    }

    private void initChart() {
        //initial dialu sleep chart
        int[] fakeData = {  };
//        int[] fakeData = {2, 1, 2, 1, 2, 2, 2, 2, 2, 1,
//                          2, 1, 2, 1, 2, 2, 2, 2, 2, 1,
//                          2, 1, 2, 1, 2, 2, 2, 2, 2, 1};
        ArrayList<BarEntry> barEntries = new ArrayList<>();
        for(int i=0;i<fakeData.length;i++) {
            barEntries.add(new BarEntry(i, fakeData[i]));
        }

        dialySleepChart.getXAxis().setPosition(XAxis.XAxisPosition.BOTTOM);
        dialySleepChart.getXAxis().setDrawLabels(false);
        dialySleepChart.getDescription().setEnabled(false);
        dialySleepChart.getLegend().setEnabled(false);
        dialySleepChart.getAxisLeft().setDrawGridLines(false);
        dialySleepChart.getAxisLeft().setDrawLabels(false);
        dialySleepChart.getAxisRight().setDrawGridLines(false);
        dialySleepChart.getAxisRight().setDrawLabels(false);
        dialySleepChart.getXAxis().setDrawGridLines(false);
        dialySleepChart.getAxisLeft().setAxisMinimum(0);
        dialySleepChart.getAxisLeft().mAxisMinimum = 0;
        dialySleepChart.getAxisLeft().mAxisMaximum = 2;

        BarDataSet bardataset = new BarDataSet(barEntries, "Sleep");
        bardataset.setColor(ContextCompat.getColor(v.getContext(), R.color.colorPrimary));
        BarData data = new BarData(bardataset);
        data.setDrawValues(false);
        dialySleepChart.setData(data);
        dialySleepChart.animateY(1000);

        CustomBarChartRender barChartRender = new CustomBarChartRender(dialySleepChart, dialySleepChart.getAnimator(), dialySleepChart.getViewPortHandler());
        barChartRender.setRadius(10);
        dialySleepChart.setRenderer(barChartRender);

    }
